import React from 'react';
import AppIcon from './AppIcon';
import { apps } from '../appsConfig';

function AppGrid() {
  const handleAppClick = (app) => {
    console.log('App clickeada:', app.name);
    // Esta funci칩n se llamar치 para apps que no sean web
    // Las apps web se abren directamente en AppIcon
  };

  return (
    <div className="flex-1 p-6 overflow-auto">
      <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-5 lg:grid-cols-6 gap-4 max-w-6xl mx-auto">
        {apps.map((app) => (
          <AppIcon 
            key={app.id}
            app={app}
            onClick={handleAppClick}
          />
        ))}
      </div>

      {/* Mensaje informativo */}
      <div className="mt-8 text-center text-gray-400 text-sm">
        <p>游깷 Aplicaciones web disponibles: {apps.length}</p>
        <p>Haz click en cualquier icono para abrir la app</p>
      </div>
    </div>
  );
}

export default AppGrid;
