import React from 'react';

function AppIcon({ name, icon, color, onClick }) {
  return (
    <div 
      className="flex flex-col items-center p-4 rounded-xl bg-gray-800 bg-opacity-50 active:scale-95 transition-transform"
      style={{ '--icon-color': color }}
      onClick={onClick}
    >
      <div className="w-14 h-14 mb-2 flex items-center justify-center">
        <img 
          src={icon} 
          alt={name} 
          className="w-full h-full object-contain"
          style={{ filter: `drop-shadow(0 0 8px var(--icon-color))` }}
        />
      </div>
      <span className="text-sm font-medium text-center">{name}</span>
    </div>
  );
}

export default AppIcon;
