import React from 'react';

function AppIcon({ app, onClick }) {
  const handleClick = () => {
    if (app.type === 'web' || app.type === 'browser') {
      // Abrir URL directamente en el navegador
      window.open(app.url, '_blank');
    } else if (onClick) {
      // Para otros tipos de apps, usar el callback
      onClick(app);
    }
  };

  return (
    <div 
      className="flex flex-col items-center p-4 rounded-xl bg-gray-800 bg-opacity-50 active:scale-95 transition-transform cursor-pointer hover:bg-opacity-70"
      style={{ '--icon-color': app.color }}
      onClick={handleClick}
    >
      <div className="w-14 h-14 mb-2 flex items-center justify-center">
        <img 
          src={app.icon} 
          alt={app.name} 
          className="w-full h-full object-contain"
          style={{ filter: `drop-shadow(0 0 8px var(--icon-color))` }}
        />
      </div>
      <span className="text-sm font-medium text-center">{app.name}</span>
    </div>
  );
}

export default AppIcon;
