import React from 'react';
import AppIcon from './AppIcon';
import { apps } from '../appsConfig';

function AppGrid() {
  return (
    <div className="grid grid-cols-4 gap-4 p-4">
      {apps.map((app) => (
        <AppIcon 
          key={app.id}
          name={app.name}
          icon={app.icon}
          color={app.color}
          onClick={() => window.open(app.url, '_self')}
        />
      ))}
    </div>
  );
}

export default AppGrid;
